mapper.select:

select c_no, no, reply, writer, c_date, updatedate
from
(
select /*+INDEX(media_comment idx_reply) */
	rownum rn, c_no, no, reply, writer, c_date, updatedate
from media_comment
where no = #{no}
and c_no > 0
and rownum <= #{cri.pageNum} * #{cri.amount}
) where rn > (#{cri.pageNum} -1) * #{cri.amount}
