mapper.select:
SELECT COUNT(like_no) as cnt
FROM history_like
WHERE media_no=#{media_no} AND id=#{id}

mapper.update:
UPDATE history_like
SET like_check = like_check + 1
WHERE id=#{id} AND media_no=#{media_no}

UPDATE history_like
SET like_check = 0
WHERE id=#{id} AND media_no=#{media_no}

mapper.select:
SELECT *
FROM history_like
WHERE media_no=#{media_no} AND id=#{id}

mapper.update:
UPDATE media
SET m_like = m_like + 1
WHERE no=#{media_no}

UPDATE media
SET m_like = m_like - 1
WHERE no=#{media_no}

mapper.select:
select like_check
from history_like
where media_no=#{no} and id=#{id}
